-- XafGitHubCopilot PostgreSQL Schema & Seed Data
-- Matches the EF Core entities from the Module project

-- Drop tables if they exist (in dependency order)
DROP TABLE IF EXISTS "OrderItems" CASCADE;
DROP TABLE IF EXISTS "Orders" CASCADE;
DROP TABLE IF EXISTS "Invoices" CASCADE;
DROP TABLE IF EXISTS "Products" CASCADE;
DROP TABLE IF EXISTS "Categories" CASCADE;
DROP TABLE IF EXISTS "Suppliers" CASCADE;
DROP TABLE IF EXISTS "Shippers" CASCADE;
DROP TABLE IF EXISTS "Customers" CASCADE;
DROP TABLE IF EXISTS "EmployeeTerritories" CASCADE;
DROP TABLE IF EXISTS "Employees" CASCADE;
DROP TABLE IF EXISTS "Departments" CASCADE;
DROP TABLE IF EXISTS "Territories" CASCADE;
DROP TABLE IF EXISTS "Regions" CASCADE;
DROP TABLE IF EXISTS "ReportDataV2" CASCADE;

-- ============================================================
-- SCHEMA
-- ============================================================

CREATE TABLE "Regions" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "Name" VARCHAR(100),
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "Territories" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "Name" VARCHAR(100),
    "RegionId" UUID REFERENCES "Regions"("ID") ON DELETE SET NULL,
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "Departments" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "Name" VARCHAR(128),
    "Code" VARCHAR(64),
    "Location" VARCHAR(256),
    "Budget" DECIMAL(18,2),
    "IsActive" BOOLEAN DEFAULT TRUE,
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "Employees" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "FirstName" VARCHAR(64),
    "LastName" VARCHAR(64),
    "Title" VARCHAR(128),
    "HireDate" TIMESTAMP,
    "Email" VARCHAR(128),
    "Phone" VARCHAR(32),
    "ReportsToId" UUID REFERENCES "Employees"("ID") ON DELETE SET NULL,
    "DepartmentId" UUID REFERENCES "Departments"("ID") ON DELETE SET NULL,
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "EmployeeTerritories" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "EmployeeId" UUID NOT NULL REFERENCES "Employees"("ID") ON DELETE CASCADE,
    "TerritoryId" UUID NOT NULL REFERENCES "Territories"("ID") ON DELETE CASCADE,
    "OptimisticLockField" INTEGER DEFAULT 0,
    UNIQUE ("EmployeeId", "TerritoryId")
);

CREATE TABLE "Categories" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "Name" VARCHAR(128),
    "Description" VARCHAR(512),
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "Suppliers" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "CompanyName" VARCHAR(128),
    "ContactName" VARCHAR(128),
    "Phone" VARCHAR(32),
    "Email" VARCHAR(128),
    "Address" VARCHAR(256),
    "City" VARCHAR(64),
    "Country" VARCHAR(64),
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "Products" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "Name" VARCHAR(128),
    "UnitPrice" DECIMAL(18,2),
    "UnitsInStock" INTEGER,
    "Discontinued" BOOLEAN DEFAULT FALSE,
    "CategoryId" UUID REFERENCES "Categories"("ID") ON DELETE SET NULL,
    "SupplierId" UUID REFERENCES "Suppliers"("ID") ON DELETE SET NULL,
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "Shippers" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "CompanyName" VARCHAR(128),
    "Phone" VARCHAR(32),
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "Customers" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "CompanyName" VARCHAR(128),
    "ContactName" VARCHAR(128),
    "Phone" VARCHAR(32),
    "Email" VARCHAR(128),
    "Address" VARCHAR(256),
    "City" VARCHAR(64),
    "Country" VARCHAR(64),
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "Invoices" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "InvoiceNumber" VARCHAR(32) UNIQUE,
    "InvoiceDate" TIMESTAMP NOT NULL,
    "DueDate" TIMESTAMP,
    "Status" INTEGER DEFAULT 0,
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "Orders" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "OrderDate" TIMESTAMP NOT NULL,
    "RequiredDate" TIMESTAMP,
    "ShippedDate" TIMESTAMP,
    "Freight" DECIMAL(18,2),
    "ShipAddress" VARCHAR(256),
    "ShipCity" VARCHAR(64),
    "ShipCountry" VARCHAR(64),
    "Status" INTEGER DEFAULT 0,
    "CustomerId" UUID REFERENCES "Customers"("ID") ON DELETE SET NULL,
    "EmployeeId" UUID REFERENCES "Employees"("ID") ON DELETE SET NULL,
    "ShipperId" UUID REFERENCES "Shippers"("ID") ON DELETE SET NULL,
    "InvoiceId" UUID REFERENCES "Invoices"("ID") ON DELETE SET NULL,
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "OrderItems" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "UnitPrice" DECIMAL(18,2),
    "Quantity" INTEGER,
    "Discount" DECIMAL(5,2),
    "OrderId" UUID REFERENCES "Orders"("ID") ON DELETE CASCADE,
    "ProductId" UUID REFERENCES "Products"("ID") ON DELETE SET NULL,
    "OptimisticLockField" INTEGER DEFAULT 0
);

CREATE TABLE "ReportDataV2" (
    "ID" UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    "DisplayName" VARCHAR(256),
    "Content" BYTEA,
    "DataTypeName" VARCHAR(512),
    "DataTypeCaption" VARCHAR(512),
    "IsInplaceReport" BOOLEAN NOT NULL DEFAULT false,
    "IsPredefined" BOOLEAN NOT NULL DEFAULT false,
    "ParametersObjectTypeName" VARCHAR(512),
    "PredefinedReportTypeName" VARCHAR(512),
    "GCRecord" INTEGER,
    "OptimisticLockField" INTEGER DEFAULT 0
);

